      wikino
      ,userno
      ,title
      ,contents
      ,registerdate
      ,updatedate
    )
  values
    (   
    	#{wikino}      
      ,#{userno}
      ,#{title}
      ,#{contents}
      ,now()
      ,now()
     )

mapper.select:
select count(*)
 from tb_wiki
where section = #{section}
