mapper.insert:


    SELECT max(wikino) FROM tb_wiki 

    Insert
	 into
  	  tb_wiki_history(  	  	
      wikino
      ,userno
      ,title
      ,contents
      ,registerdate
      ,updatedate
    )
  values
    (   
    	#{wikino}      
      ,#{userno}
      ,#{title}
      ,#{contents}
      ,now()
      ,now()
     )
