mapper.update:
UPDATE 
	tb_users_detail 
SET 
	activeat = #{activeat}
WHERE 
	enterpriseno = #{enterpriseno}
	AND userno = #{userno}

UPDATE 
	tb_users_detail 
SET 
	userat = #{userat}
WHERE 
 userno = #{userno}

