mapper.insert:
    SELECT nvl(max(seq),0)+1 FROM tb_company

	Insert
	 into
  	  tb_company(  	  	
       seq
      ,bizcd
      ,biznm
      ,biznm_en
      ,biztype
      ,bizkind
      ,bizno
      ,rprsnt_nm
      ,addr1
      ,addr2
      ,tele
      ,fax
      ,biz_auth_role
      ,reg_date
    )
  values
    (   
    	#{seq}
      ,#{bizcd}
      ,#{biznm}
      ,#{biznm_en}
      ,#{biztype}
      ,#{bizkind}
      ,#{bizno}
      ,#{rprsnt_nm}
      ,#{addr1}
      ,#{addr2}
      ,#{tele}
      ,#{fax}
      ,#{biz_auth_role}
      ,now()
     )



    SELECT nvl(max(seq),0)+1 FROM tb_user_mngr

	INSERT INTO 
		tb_user_mngr
			(   seq
			   ,bizcd  	
		      ,user_id 
		      ,user_passwd
		      ,user_nm 
		      ,user_email  
		      ,user_hp 
