		      ,user_dept
		      ,user_role
		      ,proc_stat
		      ,reg_date

		    ) VALUES
			    (  #{seq}
	    		   ,#{bizcd}
			      ,#{user_id} 
			      ,#{user_passwd1}
			      ,#{user_nm} 
			      ,#{user_email} 
			      ,#{user_hp} 
			      ,#{user_rank} 
			      ,#{user_dept}
			      ,#{user_role}
			      ,#{proc_stat}
			      ,now()
			     )

mapper.select:
select COUNT(*) 
from tb_company 
where bizno= #{bizno};

select COUNT(*) 
from tb_user_mngr 
where user_id= #{user_id};

select COUNT(*) 
from tb_user_mngr 
where user_id= #{user_id};

select user_nm, user_dept, user_rank, user_hp, user_email, user_id
from tb_user_mngr
where user_id= #{user_id};

select seq, user_id, user_passwd, user_nm, user_dept, user_rank, user_email ,active_status
from tb_user_mngr
where user_role != 'r' and delete_at != 1 and bizcd = #{bizcd}
order by seq desc;

select user_role 
from tb_user_mngr
where user_id = #{user_id}
order by seq desc;

mapper.update:
update tb_user_mngr
user_passwd=#{user_passwd}, user_nm=#{user_nm}, user_dept=#{user_dept}, user_rank=#{user_rank}, user_hp = #{user_hp}, user_email=#{user_email}, updt_date = now()
where user_id = #{user_id};

update tb_user_mngr
user_nm = #{user_nm}, user_dept = #{user_dept}, user_rank = #{user_rank}, user_hp = #{user_hp}, user_email = #{user_email}, updt_date = now()
where user_id = #{user_id};

update tb_user_mngr
active_status = #{active_status}, proc_stat = #{proc_stat}, updt_date = now()
where user_id = #{user_id};
